import React from 'react';
import HeroSection from '../content/HeroSection';
import HeroImg from '../images/aboutImg2.png'
import { motion } from 'framer-motion';

const pageVariants = {
    initial: {
      opacity: 0,
      x: "-100vw",
      scale: 0.8
    },
    in: {
      opacity: 1,
      x: 0,
      scale: 1
    },
    out: {
      opacity: 0,
      x: "100vw",
      scale: 1.2
    }
  };
  
  const pageTransition = {
    type: "tween",
    ease: "anticipate",
    duration: 0.5
  };
  
  const pageStyle = {
    position: "absolute"
  };


const About = () => {

    return (
        <>   
        <motion.div
        style={pageStyle}
        initial="initial"
        animate="in"
        exit="out"
        variants={pageVariants}
        transition={pageTransition}
      >
            <HeroSection 
            titleBefore="Welcome"
            titleAfterBr="to"
            titleStrong="About Page"
            description="We are world best web Designer and Developer."
            c2aText="Our Services"
            visit="/services" 
            imgSrc={HeroImg}   
            />
            </motion.div>
        </>

    );
};

export default About;
