import React, { useState } from 'react';

const Contact = () => {

    const [cData,setData] = useState({
        fName:"",
        LName:"",
        email:"",
        phone:"",
        message:""
    })

    const fromFillEvent =(event)=>{

        const {name,value} = event.target;
        setData((preVal)=>{
            return {
            ...preVal,
                [name]:value,
            }
        })
    }
    const FormSubmit =(e)=>{
        e.preventDefault();
        alert(`I am ${cData.fName} ${cData.LName} my phone no ${cData.phone}. Thank you`);
    }

    return (
        <>
          <section className="Contact_Us">
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-10 mx-auto">
                            <div className="row">
                                <div className="col-md-6 col-10 mx-auto">
                                    <div className="section_heading">
                                        <h1>Contact Us</h1>
                                    </div>
                                    <from className="contact_from" onSubmit={FormSubmit} >
                                        <div className="form-group">
                                            <label for="fName">First Name:</label>
                                            <input type="text" className="form-control" onChange={fromFillEvent} name="fName" value={cData.fName} id="fName" placeholder="Enter First Name"/>
                                        </div>
                                        <div className="form-group">
                                            <label for="LName">Last Name:</label>
                                            <input type="text" className="form-control" onChange={fromFillEvent} name="LName" value={cData.LName} id="LName" placeholder="Enter Last Name"/>
                                        </div>
                                        <div className="form-group">
                                            <label for="email">Email address:</label>
                                            <input type="email" className="form-control" onChange={fromFillEvent} name="email" value={cData.email} id="email" placeholder="liakutali@yahoo.com"/>
                                        </div>
                                        <div className="form-group">
                                            <label for="phone">Phone No:</label>
                                            <input type="number" className="form-control" onChange={fromFillEvent} name="phone" value={cData.phpne} id="phone" placeholder="880 1720 827368"/>
                                        </div>
                                        <div className="form-group">
                                            <label for="message">Password</label>
                                            <textarea className="form-control" id="message" onChange={fromFillEvent} name="message" value={cData.message} id="message" placeholder="Write Your Message " rows="3"></textarea>
                                        </div>
                                        <button type="submit" className="btn btn-primary">Submit</button>
                                    </from>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
          </section>  
        </>
    );
};

export default Contact;
